import _Message2 from "element-ui/lib/theme-chalk/message.css";
import "element-ui/lib/theme-chalk/base.css";
import _Message from "element-ui/lib/message";
import Vue from 'vue';
import Vuex from 'vuex';
import conversation from "./modules/conversation";
import group from "./modules/group";
import user from "./modules/user";
import video from "./modules/video";
import friend from "./modules/friend";
import blacklist from "./modules/blacklist";
import groupLive from "./modules/groupLive";
Vue.use(Vuex);
export default new Vuex.Store({
  state: {
    current: Date.now(),
    // 当前时间
    intervalID: 0,
    message: undefined
  },
  getters: {
    hidden: function hidden(state) {
      // eslint-disable-next-line no-unused-vars
      var temp = state.current;

      if (typeof document.hasFocus !== 'function') {
        return document.hidden;
      }

      return !document.hasFocus();
    }
  },
  mutations: {
    startComputeCurrent: function startComputeCurrent(state) {
      state.intervalID = setInterval(function () {
        state.current = Date.now();
      }, 500);
    },
    stopComputeCurrent: function stopComputeCurrent(state) {
      clearInterval(state.intervalID);
      state.intervalID = 0;
    },
    showMessage: function showMessage(state, options) {
      if (state.message) {
        state.message.close();
      }

      state.message = _Message({
        message: options.message,
        type: options.type || 'success',
        duration: options.duration || 2000,
        offset: 40
      });
    }
  },
  modules: {
    conversation: conversation,
    group: group,
    friend: friend,
    blacklist: blacklist,
    user: user,
    video: video,
    groupLive: groupLive
  }
});
