result:
.cursor {
  cursor: pointer;
}
.active {
  color: #2d8cf0
}
.setting-icon {
  display flex
  justify-content center
  align-items center
  height 40px
  margin 0 10px
}
.player {
  position relative
  width 99.9%
  height 100%
  display flex
  flex-flow column
  .video-container {
    position relative
    display flex
    flex-direction column
    justify-content center
    align-items center
    flex 1
    background rgba(0, 0, 0, 0.3)
    .stop-camera {
      position absolute
      top 0
      right 0
      bottom 0
      left 0
      background-color #ffffff
      display flex
      flex-direction column
      justify-content center
      align-items center
    }
  }
  .header-bar {
    position: relative;
    width 100%
    height 70px
    background-color #363e47
    padding 10px 10px 10px 20px
  }
  .setting-bar {
    position: relative;
    bottom 0
    width 100%
    height 55px
    background-color #363e47
    .player-time{
      position: absolute;
      bottom 0
      left 15px
      width 100%
      line-height 60px
      font-size: 14px;
      color: #D2DAE6;
      letter-spacing: 0;
    }
    .player-start {
      position: absolute;
      right 0
      bottom 0
      width 210px
      height 55px
      background #5cadff
      display: flex;
      justify-content: center;
      align-items: center;
    }
    .player-mic{
      position: absolute;
      right 260px
      bottom 0
      /*width 210px*/
      height 55px
      display: flex;
      justify-content: center;
      align-items: center
      padding 0 10px
    }
    .play-text{
      font-size 16px
      color #ffffff
      margin-left 5px
    }
    .player-icon{
      width 14px
      height 14px
    }
    .mic-text{
      font-size 14px
      color #8A9099
      margin-left 5px
    }
  }
  .volume-box {
    position absolute
    right 220px
    bottom 8px
    .progress-box {
      padding 4px 0
      position absolute
      bottom 35px
      right 32px
    }
  }
  /deep/ .vcp-player video {
    position absolute
    top 0
    left 0
    width 100% !important
    height 100% !important
    object-fit cover
  }

  /deep/ .vcp-player {
    width 100% !important
    height 100% !important
    position: relative;
    display: flex;
    justify-content: center;
    align-items: center;
    background-color:rgba(0,0,0,0)
  }

  /deep/ .vcp-bigplay {
    display none
  }

  /deep/ .vcp-error-tips {
    color #FFFFFF
    margin-top: -8.25em
  }
}
