::-webkit-input-placeholder {
  color: #fff
}
::-moz-input-placeholder {
  color: #fff
}
::-ms-input-placeholder {
  color: #fff
}
.cursor {
  cursor: pointer;
}
.active {
  color: #2d8cf0
}
.pusher {
  position relative
  width 100%
  height 100%
  background rgba(0, 0, 0, 1)
  display flex
  flex-flow column
  flex-direction column
  .video-container {
    position relative
    height calc(100% - 125px)
    .stop-camera {
      position absolute
      top 0
      right 0
      bottom 0
      left 0
      background-color #ffffff
      display flex
      flex-direction column
      justify-content center
      align-items center
    }
  }
  .header-bar {
    position: relative;
    width 100%
    height 70px
    background-color #363e47
    padding 10px 10px 10px 20px
    .input-name-box{
      position absolute
      left 0
      top 0px
      width calc(100% - 100px)
      height 100%
      display flex
      align-items center
      z-index 99
      padding 10px 20px
      .avatar{
        width 50px
        height 50px
        border-radius 50%
        margin 0px 10px 0px 0px
      }
      .room-name{
        border hidden
        outline-style none
        height 40px
        width  60%
        font-size 20px
        color #fff
        background rgba(255, 255,255, 0)
        border-bottom 1px solid #fff
      }
    }
  }
  .setting-bar {
    position: relative;
    width 100%
    height 55px
    background-color #363e47
    .pusher-start {
      position: absolute;
      right 0
      bottom 0
      width 210px
      height 55px
      background #5cadff
      display: flex;
      justify-content: center;
      align-items: center;
    }
    .pusher-mic{
      position: absolute;
      right 220px
      bottom 0
      /*width 210px*/
      height 55px
      display: flex;
      justify-content: center;
      align-items: center
      padding 0 10px
    }
    .play-text{
      font-size 16px
      color #ffffff
      margin-left 5px
    }
    .pusher-icon{
      width 14px
      height 14px
    }
    .mic-text{
      font-size 14px
      color #8A9099
      margin-left 5px
    }
  }
}
